async function setFingerprintCookie() {
var htfpIDLocal = "NA";
var publisherIDLocal = "NA";
if(getCookie("_ht_fp")==null || getCookie("_ht_fp")=='NA')
{
try {
let fURL = 'https://www.hindustantimes.com/cdp/user/ht';
const controller = new AbortController();
const timeout = 3000;
const timeoutId = setTimeout(() => {
controller.abort();
}, timeout);
try {
const response = await fetch(fURL, {
method: 'GET',
credentials: 'include', // Include cookies in the request
signal: controller.signal, // Attach the AbortController signal
});
if (response.ok) {
const dataFinger = await response.json();
htfpIDLocal = dataFinger._ht_fp;
publisherIDLocal = dataFinger.ppid;
}
} finally {
clearTimeout(timeoutId);
}
} catch (error) {
console.error('Error in fetching fingerprint data:', error);
}
}
else
{
htfpIDLocal = getCookie('_ht_fp');
publisherIDLocal = getCookie('ppid');
}
