function setCookie(e, t, a, isMinute=false) {
var i = new Date;
if(isMinute){
i.setTime(i.getTime() + a * 60 * 1e3);
}else{
i.setTime(i.getTime() + 24 * a * 60 * 60 * 1e3);
}
var o = "expires=" + i.toGMTString();
const secureFlag = "; Secure"; // Ensures the cookie is only sent over HTTPS
document.cookie = e + "=" + t + ";" + o + ";path=/" + secureFlag;
}
function removeCookie(name) {
document.cookie = name +'=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
}
