function setWithExpiryCDP(key, value, ttl) {
const now = new Date()
const item = {
value: value,
expiry: now.getTime() + ttl,
}
localStorage.setItem(key, JSON.stringify(item))
}
function getWithExpiryCDP(key) {
const itemStr = localStorage.getItem(key)
// if the item doesn't exist, return null
if (!itemStr) {
return null
}
const item = JSON.parse(itemStr)
const now = new Date()
// compare the expiry time of the item with the current time
if (now.getTime() > item.expiry) {
// If the item is expired, delete the item from storage
// and return null
localStorage.removeItem(key)
return null
}
return item.value
}

function getNetworkEffectiveType() {
const connection = navigator.connection || navigator.mozConnection || navigator.webkitConnection;
const validTypes = new Set(["slow-2g", "2g", "3g", "4g"]);
if (connection && typeof connection.effectiveType === "string") {
const type = connection.effectiveType.toLowerCase();
return validTypes.has(type) ? type : "other";
}
return "other";
}
// Example usage
console.log("Network effective type:", getNetworkEffectiveType());

