const isSizeMatched = (adsData = [], actualSize = [], adUnitId = '') => {
if (adsData?.length === 0) return undefined;
// Find the array of allowed sizes for the current ad unit
const definedSizesForAdUnit = adsData?.find(
ad => ad.getAdUnitPath() === adUnitId
)?.getSizes();
// Correctly check if the rendered adSize array exists within the definedSizesForAdUnit array
const isMatch = definedSizesForAdUnit?.some(
definedSize =>
definedSize.width === actualSize[0] &&
definedSize.height === actualSize[1]
);
return isMatch;
};
const sendAlertSizeMismatchCDP = (
adUnitId,
adUnitSize,
adsData = [],
adSlotId,
) => {
if (adUnitsCDPCache.has(adSlotId)) return;
const isMisMatched = isSizeMatched(adsData, adUnitSize, adUnitId) === false;
if (!isMisMatched) return;
const cacheKey = adUnitId + document.location.href;
const adUnitIndex = adUnitsIndexCache.get(cacheKey) || 0;
dapTracker?.track(
'ad_size_mismatch',
{
url: document.location.href,
htfp_id: getCookie('_ht_fp'),
adUnitId: adUnitId || '',
adUnitSize: adUnitSize || '',
adUnitIndex: adUnitIndex,
section: section || '',
subSection: subSection || '',
pageType: pageTYPE || '',
},
'event'
);
adUnitsIndexCache.set(cacheKey, adUnitIndex + 1);
adUnitsCDPCache.set(adSlotId, true);
};
const slotRenderEndedHandler = (adsData = []) => {
if (typeof googletag === 'undefined') return;
const handleSlotHandler = event => {
const adSize = event.size;
if (adSize != null && event.slot != null) {
const adUnitId = event.slot.getAdUnitPath();
const adSlotId = event.slot.getSlotElementId();
if (pageTYPE === 'story') {
sendAlertSizeMismatchCDP(adUnitId, adSize, adsData, adSlotId);
}
}
};
googletag.cmd.push(() => {
googletag.pubads().addEventListener('slotRenderEnded', handleSlotHandler);
});
return handleSlotHandler;
};
slotRenderEndedHandler(refreshAdSlot);

