function getOfferDetails() {
try {
var contentHtml = "";
let url = "https://www.hindustantimes.com/static-content/10m/htoffers-img.json";
// url = "https://www.hindustantimes.com/static-content/10m/htoffers.json";
// url = "https://htoffers.thelogicalbanya.com/api/getRewardsInfo.php?clientID=htoffers4H4vMTAV9AO8U4XV&key=mmZ7eyLNDAOLv3W0mX43RXl6";
$.ajax({
url: url,
dataType: "json",
type: "GET",
success: function (res) {
// var offerJson = JSON.parse(AES256.decrypt(res.Message, 'X0r&9i7@skq@0zGzSx^P'));
var offerJson = res;
var rewardsText = offerJson.rewardsText;
var rewardsCount = offerJson.rewardsCount;
var rewardsValue = offerJson.rewardsValue;
contentHtml += '
';
contentHtml += '' + rewardsText;
contentHtml += '';
contentHtml += '
';
contentHtml += '';
offerJson.brandLogos.forEach(function (elm, index) {
if (index < 4) {
contentHtml += '
';
// contentHtml += '';
contentHtml += '';
// contentHtml += '';
contentHtml += ' ';
}
});
contentHtml += '

+' + rewardsCount + ' More ';
contentHtml += '';
contentHtml += '';
if ($("#excitingOfferId").length > 0) {
$("#excitingOfferId").html(contentHtml);
}
if ($("#offerTextId").length > 0) {
$("#offerTextId").html(rewardsCount + ' ' + 'remaining');
}
}
});
} catch (err) { }
}
if (getCookie("ht-location") === "IN" && (!navigator.userAgent.includes("Chrome-Lighthouse"))) {
googletag.cmd.push(function () {
var cookieValue = getCookie("_ht_fp");
// var cookieValue = "fc99f1a2-a720-4988-a6d1-91b20a7815d9";
//var htCdp = getCookie("ht_cdp");
var htCdpLS = getWithExpiryCDP("ht_cdp");
console.log("After GPT:", new Date());
var requestUrl = "https://www.hindustantimes.com/cdp/api/adserve/ht?user=" + cookieValue;
if (!htCdpLS && cookieValue) {
var xhr = new XMLHttpRequest();
xhr.open('GET', requestUrl);
xhr.timeout = 700;
xhr.responseType = 'json';
xhr.onload = function () {
var campaigns = '';
var jsonData = xhr.response;
if (jsonData !== '' && jsonData.campaigns !== undefined) {
campaigns = jsonData.campaigns;
setWithExpiryCDP("ht_cdp", JSON.stringify(campaigns), 86400000);
googletag.pubads().setTargeting('cdpcampaign', campaigns);
} else {
}
};
xhr.onerror = function () { };
xhr.send(null);
} else {
googletag.pubads().setTargeting('cdpcampaign', JSON.parse(htCdpLS));
}
});
}
function popupInterestialJs() {
googletag.cmd.push(function () {
let popupInterstitialSlot;
let popupInterstitialAdCode = "";
if (isDesktop) {
popupInterstitialAdCode = "/1055314/Hindustantimes_Desktop_Across/HT_Desk_Popup_Interstitial";
} else {
popupInterstitialAdCode = "/1055314/Hindustantimes_Mweb_Across/HT_WAP_Popup_Interstitial";
}
popupInterstitialSlot = googletag.defineOutOfPageSlot(popupInterstitialAdCode, "div-gpt-ad-interstitialPopupAds-0");
if (popupInterstitialSlot) {
popupInterstitialSlot.addService(googletag.pubads());
}
googletag.pubads().refresh([popupInterstitialSlot]);
});
}
