var topSlot, rightSlot, storySlot, gutterAdCode = '/1055314/Hindustantimes_Desktop_Skyscrapper/HT_Desk_skyscrapper';
const definedSlots = {};
const setupAdSlots = (pageType, deviceType, isFirstStoryAd) => {
const config = adConfigs[pageType][deviceType];
if (!config) return;
const arg = [];
if (deviceType === 'desktop') {
if (!definedSlots[config.topDiv]) {
topSlot = googletag.defineSlot(config.topAdCode, config.topSizes, config.topDiv).addService(googletag.pubads());
definedSlots[config.topDiv] = true;
arg.push({code: config.topAdCode, divId: config.topDiv});
}
if (!definedSlots[config.rightDiv]) {
rightSlot = googletag.defineSlot(config.rightAdCode, config.rightSizes, config.rightDiv).addService(googletag.pubads());
definedSlots[config.rightDiv] = true;
arg.push({code: config.rightAdCode, divId: config.rightDiv});
}
gutterAdCode = config.gutterAdCode;
} else if (deviceType === 'mobile') {
if (isFirstStoryAd) {
if (!definedSlots[config.firstDiv]) {
storySlot = googletag.defineSlot(config.firstAdCode, config.firstSizes, config.firstDiv).addService(googletag.pubads());
definedSlots[config.firstDiv] = true;
arg.push({code: config.firstAdCode, divId: config.firstDiv});
}
} else {
if (!definedSlots[config.topDiv]) {
topSlot = googletag.defineSlot(config.topAdCode, config.topSizes, config.topDiv).addService(googletag.pubads());
definedSlots[config.topDiv] = true;
arg.push({code: config.topAdCode, divId: config.topDiv});
}
}
}
if (prebidActivate && arg.length > 0) {
executePrebidForSection(arg);
}
}
const defineAdSlots = function () {
googletag.cmd.push(function () {
const oyoAdSections = ["cricket", "trending", "sports"];
