const maxAttemptsOnboarding = 4;
function loadOnboardingSdk(sourceOfOnboarding) {
if (typeof onboarding != "undefined") {
window.onboarding.render({
element: document.querySelector('#onBoardingDiv'),
client: 'HT',
funnel_entry: sourceOfOnboarding,
campaignId: getCookie('ht_cdp_echo_campaignId') || null,
campaignName: getCookie('ht_cdp_echo_campaignName') || null,
campaignType: 'onboarding',
clientToken: getCookie('token') || null,
clientProperty: 'HT Web',
});
} else {
ctrOnboarding++;
if(ctrOnboarding <= maxAttemptsOnboarding) {
setTimeout(() => {
loadScriptPltU('https://www.hindustantimes.com/ht-img/sso/prod/onboarding/onboarding-sdk.js?v=1'
,function() {
loadOnboardingSdk(sourceOfOnboarding);
}
);
}, 2000);
}
}
