function checkLocationAndDevice(widget, isMobile, inventory, performanceType, isPremium) {
const device = isMobile ? 'mweb' : 'web';
const location = getCookie('ht-location') == 'IN' ? 'india' : 'rest-of-world';
let showEchoInventory = false;
if (inventory && inventory[widget]) {
Object.values(inventory[widget])?.forEach(element => {
if (
element.pageType.includes(pageTYPE.toLowerCase() || 'section') &&
(element.performanceType.includes('all') ||
element.performanceType.includes(performanceType)) &&
(
element.isPremium.includes("both") ||
(isPremium && element.isPremium.includes("yes")) ||
(!isPremium && element.isPremium.includes("no"))
) &&
element.platform.includes(device) &&
element.geography.includes(location)
) {
showEchoInventory = true;
}
});
}
return showEchoInventory;
}
function getParameterByName(name, url = window.location.href) {
name = name.replace(/[\[\]]/g, '\\$&');
var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
results = regex.exec(url);
if (!results) return null;
if (!results[2]) return '';
return decodeURIComponent(results[2].replace(/\+/g, ' '));
}
