function addPreloadTag(s) {
var gptPreload = document.createElement('script');
if(s) { gptPreload.rel = 'preload'; }
gptPreload.async = true;
gptPreload.as = 'script';
gptPreload.type = 'text/javascript';
gptPreload.src = 'https://securepubads.g.doubleclick.net/tag/js/gpt.js';
var node = document.getElementsByTagName('script')[0];
node.parentNode.insertBefore(gptPreload, node);
var apsPreload = document.createElement('script');
if(s) { apsPreload.rel = 'preload'; }
apsPreload.async = true;
apsPreload.as = 'script';
apsPreload.type = 'text/javascript';
apsPreload.src = 'https://c.amazon-adsystem.com/aax2/apstag.js';
var node1 = document.getElementsByTagName('script')[0];
node1.parentNode.insertBefore(apsPreload, node1);
var pwtPreload = document.createElement('script');
if(s) { pwtPreload.rel = 'preload'; }
pwtPreload.async = true;
pwtPreload.as = 'script';
pwtPreload.type = 'text/javascript';
pwtPreload.src = '//ads.pubmatic.com/AdServer/js/pwt/156370/2494/pwt.js';
var node2 = document.getElementsByTagName('script')[0];
node2.parentNode.insertBefore(pwtPreload, node2);
}
