function closeTooltip() {
var x = document.getElementById("toolTip");
if(x)
{
if (x.style.display === "none") {
x.style.display = "block";
} else {
x.style.display = "none";
}
}
}
var myOffer = getCookie("HTMyOffer");
if (myOffer != null && myOffer != "") {
var x = document.getElementById("toolTip");
if(x)
x.style.display = "none";
}else{
setCookie("HTMyOffer", 'myOfferHT', 10);
var x = document.getElementById("toolTip");
if(x)
x.style.display = "block";
}
getCookie = function (e) {
for (var a = e + "=", t = document.cookie.split(";"), n = 0; n < t.length; n++) {
for (var o = t[n];
" " == o.charAt(0);) o = o.substring(1, o.length);
if (0 == o.indexOf(a)) return o.substring(a.length, o.length)
}
return null;
}
setCookie = function (e, t, a) {
var i = new Date;
i.setTime(i.getTime() + 24 * a * 60 * 60 * 1e3);
var o = "expires=" + i.toGMTString();
const secureFlag = "; Secure"; // Ensures the cookie is only sent over HTTPS
document.cookie = e + "=" + t + ";" + o + ";domain=.hindustantimes.com;path=/" + secureFlag;
}

function openAppTracking() {
let pageTypeBookmark = "story";
let articleTitle = "";
let articleCategory = "";
let articleId = "";
let title = $(".hdg1");
if(title.length>0) {
articleTitle = title[0].innerText.replace("'","");
}
if (typeof $('#dataHolder').attr("data-story-section") !== "undefined") {
articleCategory = $('#dataHolder').attr("data-story-section");
}
if (typeof $('#dataHolder').attr("data-story-id") !== "undefined") {
articleId = $('#dataHolder').attr("data-story-id");
}
dataLayer.push({
'event': 'widget_clicked',
'widget_name': 'open_app_click',
'page_type': pageTypeBookmark,
'article_title': articleTitle,
'article_category': articleCategory,
'story_id': articleId,
'user_login_status': (getCookie('token') && !getCookie('token').includes('amp-') ? 'logged_in':'non_logged_in'),
'user_id': (getCookie('token') && !getCookie('token').includes('amp-') ? getCookie('_ht_clientid'):''),
'login_status': (getCookie('token') && !getCookie('token').includes('amp-') ? 'logged_in':'non_logged_in'),
'clientID': (getCookie('token') && !getCookie('token').includes('amp-') ? getCookie('_ht_clientid'):'NA')
});
}
function commonGA4Tracking(eventName,ctaText) {
let pageTypeBookmark = "story";
let articleTitle = "";
let articlePublishedDate = "";
let articleCategory = "";
let articleId = "";
let title = $(".hdg1");
if(title.length>0) {
articleTitle = title[0].innerText.replace("'","");
}
let publishElement = $(".dateTime.secTime.storyPage")
if(publishElement.length>0) {
articlePublishedDate = publishElement[0].innerText;
}
if (typeof $('#dataHolder').attr("data-story-section") !== "undefined") {
articleCategory = $('#dataHolder').attr("data-story-section");
}
if (typeof $('#dataHolder').attr("data-story-id") !== "undefined") {
articleId = $('#dataHolder').attr("data-story-id");
}
dataLayer.push({
'event': eventName,
'page_type': pageTypeBookmark,
'article_title': articleTitle,
'cta_text' : ctaText,
'publish_date': articlePublishedDate,
'article_category': articleCategory,
'article_id': articleId,
'logged_in_status': (getCookie('token') && !getCookie('token').includes('amp-') ? 'logged_in':'non_logged_in'),
'user_ID': (getCookie('token') && !getCookie('token').includes('amp-') ? getCookie('_ht_clientid'):''),
'login_status': (getCookie('token') && !getCookie('token').includes('amp-') ? 'logged_in':'non_logged_in'),
'clientID': (getCookie('token') && !getCookie('token').includes('amp-') ? getCookie('_ht_clientid'):'NA')
});
}

