let modulesLoadedOnScroll = false;
const modulesToLazyLoad = [
'vouchersdk',
'onBoardingSdk',
'autoSDK',
'autoLeadFormSdkJs'
];
function loadModulesOnScroll() {
const shouldLoad = getCookie("ht-location") == "IN";
const scrollThreshold = 100;
if (shouldLoad && !modulesLoadedOnScroll && window.scrollY > scrollThreshold) {
window.removeEventListener('scroll', loadModulesOnScroll);
modulesLoadedOnScroll = true;
require(modulesToLazyLoad);
}
}
window.addEventListener('scroll', loadModulesOnScroll);
// Define fallback function
function handleEchoSdkFailure() {
// Custom fallback logic
if(window.initGoogleOneTap) {
window.initGoogleOneTap(true);
} else {
console.warn("initGoogleOneTap function not defined.");
}
}
const requirejsOnError = function(err) {
console.error("RequireJS load error:", err);
if (err.requireType === 'scripterror' && err.requireModules?.includes('htechosdk')) {
console.warn("htechosdk failed to load. Executing fallback...");
handleEchoSdkFailure();
} else {
throw err;
}
};

