function loadCssFile(e, t) {
var l, d, e = e;
document.getElementById(e) || (l = document.getElementsByTagName("head")[0],
(d = document.createElement("link")).id = e,
d.rel = "stylesheet",
d.type = "text/css",
d.href = t,
d.media = "all",
l.appendChild(d))
}
