function removeCookieTRC(cname, cvalue, exdays) {
var d = new Date();
d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
var domain = ";path=/";
var expires = "expires=" + d.toUTCString();
document.cookie = cname + "=" + cvalue + ";" + expires + domain + ";path=/";
}

function removeCookie(name) {
document.cookie = name + '=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
document.cookie = name + '=; domain=.www.hindustantimes.com; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
document.cookie = name + '=; domain=.hindustantimes.com; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
}
